import React from 'react'
import './signup.css'

class Signup extends React.Component {
    constructor(props){
        super(props)
        this.init = {
            name : 'name',
            email : 'email',
            password : 'password'
        }
        this.state = {
            userData : {
                name : 'name',
                email : 'email',
                password : 'password'
            },
            password : {
                password1 : 'password1',
                password2 : 'password2'
            }            
        }
    }

    updateName(event){
        // this.setState({ value : event.target.value })
        console.log(event.target.name)
    }

    isValidInfo(){
        const init = this.init
        const user = this.state.userData
        if (init.name === user.name || init.email === user.email || init.password === user.password) {
            console.log('false')
            return false
        }

        return true
    }

    async createAccountHandler(){
        // update state
        // const user = {
        //     name : 
        // }

        // validate userinfo
        if (!this.isValidInfo()){
            console.log('User info not valid')
        }
        else {
            console.log('create account ')
        }
    }

    handleSubmit(event){
        console.log('submitting')
        event.preventDefault()
    }

    render() {
        return (
            <div className='signup-body'>
                <h1>Signup</h1>
                <div class="container">
                    <form onSubmit={this.handleSubmit}>
                        <div class="form-group">
                            <input type="text" name="name"  class="form-control my-input" id="name" placeholder="Name" onChange={this.updateName}/>
                        </div>
                        <div class="form-group">
                            <input type="email" name="email"  class="form-control my-input" id="email" placeholder="Email" onChange={this.updateName}/>
                        </div>


                        <div class="form-group">
                            <input type="password" min="0" name="password" id="password1"  class="form-control my-input" placeholder="Password" />
                        </div>
                        <div class="form-group">
                            <input type="password" min="0" name="password" id="password2"  class="form-control my-input" placeholder="Confirm Password" />
                        </div>


                        <div class="text-center ">
                            <button class=" btn btn-block send-button tx-tfm" onClick={this.createAccountHandler}>Create Your Free Account</button>
                        </div>
                        <div class="col-md-12 ">
                            <div class="login-or">
                                <hr class="hr-or" />
                                <span class="span-or">or</span>
                            </div>
                        </div>
                        <div class="form-group">
                            <a class="btn btn-block g-button" href="#">
                            <i class="fa fa-google"></i> Sign up with Google
                            </a>
                        </div>


                        <p class="small mt-3">By signing up, you are indicating that you have read and agree to the <a href="#" class="ps-hero__content__link">Terms of Use</a> and <a href="#">Privacy Policy</a>. </p>
                    </form>
                </div>
            </div>
        )
    }
}

export default Signup
