import React from 'react'
import './signup.css'
const axios = require('axios')
const apiUrl = require('../config/keys').serverUrl
const SignUpForm = require('./SignUpForm')

class SignUpContainer extends React.Component {

    constructor(props){
        super(props)
        this.handleChange = this.handleChange.bind(this)
        this.handleSubmit = this.handleSubmit.bind(this)
        this.init = {
            name : 'name',
            email : 'email',
            password : 'password'
        }
        this.state = {
            user : {
                name : 'name',
                email : 'email',
                password1 : 'password1',
                password2 : 'password2'
            }        
        }
    }

    handleChange(event){
        const field = event.target.name
        console.log(field)
        const user = this.state.user
        user[field] = event.target.value

        this.setState({
            user
        })
    }

    handleSubmit(event){
        event.preventDefault()
        const stateUser = this.state.user 
        const user = {
            name : stateUser.name,
            email : stateUser.email,
            password : stateUser.password1
        }
        console.log('submit!')
        axios.post(apiUrl.concat('/users'), user)
    }

    isValidInfo(){
        const init = this.init
        const user = this.state.user
        if (init.name === user.name || init.email === user.email || init.password === user.password) {
            console.log('false')
            return false
        }

        return true
    }

    render(){
        return (
            <SignUpForm 
                handleChange = {this.handleChange}
                handleSubmit = {this.handleSubmit}
            />
        )
    }
}
