import React from 'react'
import './signup.css'
const axios = require('axios')
const apiUrl = require('../config/keys').serverUrl

class Signup extends React.Component {
    constructor(props){
        super(props)
        this.handleChange = this.handleChange.bind(this)
        this.handleSubmit = this.handleSubmit.bind(this)
        this.init = {
            name : 'name',
            email : 'email',
            password : 'password'
        }
        this.state = {
            user : {
                name : 'name',
                email : 'email',
                password1 : 'password1',
                password2 : 'password2'
            }        
        }
    }

    handleChange(event){
        const field = event.target.name
        console.log(field)
        const user = this.state.user
        user[field] = event.target.value

        this.setState({
            user
        })
    }

    handleSubmit(event){
        event.preventDefault()

        const user = this.state.user
        console.log('submit!')
        axios.post('/users', user)
    }

    isValidInfo(){
        const init = this.init
        const user = this.state.user
        if (init.name === user.name || init.email === user.email || init.password === user.password) {
            console.log('false')
            return false
        }

        return true
    }

    // async createAccountHandler(){
    //     // update state
    //     // const user = {
    //     //     name : 
    //     // }

    //     // validate userinfo
    //     if (!this.isValidInfo()){
    //         console.log('User info not valid')
    //     }
    //     else {
    //         console.log('create account ')
    //     }
    // }


    render() {
        return (
            <div className='signup-body'>
                <h1>Signup</h1>
                <div class="container">
                    <form onSubmit={this.handleSubmit}>
                        <div class="form-group">
                            <input type="text" name="name"  class="form-control my-input" id="name" placeholder="Name" onChange={this.handleChange}/>
                        </div>
                        <div class="form-group">
                            <input type="email" name="email"  class="form-control my-input" id="email" placeholder="Email" onChange={this.handleChange}/>
                        </div>


                        <div class="form-group">
                            <input type="password" min="0" name="password1" id="password1"  class="form-control my-input" placeholder="Password" onChange={this.handleChange}/>
                        </div>
                        <div class="form-group">
                            <input type="password" min="0" name="password2" id="password2"  class="form-control my-input" placeholder="Confirm Password" onChange={this.handleChange}/>
                        </div>


                        <div class="text-center ">
                            <button type="submit" class=" btn btn-block send-button tx-tfm" onClick={this.createAccountHandler}>Create Your Free Account</button>
                        </div>
                        <div class="col-md-12 ">
                            <div class="login-or">
                                <hr class="hr-or" />
                                <span class="span-or">or</span>
                            </div>
                        </div>
                        <div class="form-group">
                            <a class="btn btn-block g-button" href="#">
                            <i class="fa fa-google"></i> Sign up with Google
                            </a>
                        </div>


                        <p class="small mt-3">By signing up, you are indicating that you have read and agree to the <a href="#" class="ps-hero__content__link">Terms of Use</a> and <a href="#">Privacy Policy</a>. </p>
                    </form>
                </div>
            </div>
        )
    }
}

export default Signup
